    private double totalpollution = 20;
    private int criticallevel = 1;
    private double wastwater = 0;
    
    public void setTotalPollution(double playerspollution){
        this.totalpollution += ((playerspollution + 365 + (this.wastwater * 0.35)) / 120);
        if(this.totalpollution > 0.39 && this.totalpollution < 0.60 ) this.criticallevel = 2;
        if(this.totalpollution > 0.59 && this.totalpollution < 0.70 ) this.criticallevel = 3;
        if(this.totalpollution > 0.79 && this.totalpollution < 0.80 ) this.criticallevel = 4;
        if(this.totalpollution > 0.89 && this.totalpollution < 0.90 ) this.criticallevel = 5;
        if(this.totalpollution > 0.99 && this.totalpollution < 1.00 ) this.criticallevel = 6;
    }
    public void setWastWater(double wastwater){
        this.wastwater = wastwater;
    }
    public double getPollution(){
        return this.totalpollution;
    }
    public int getCriticalLevel(){
        return this.criticallevel;
    }
    
    public void print(){
        System.out.println("Poluição total: "+this.totalpollution);
        System.out.println("Nivel critico de poluição atual: "+this.criticallevel);
        System.out.println("Disperdício total de água: "+this.wastwater);
    }
    
